import React, { useState } from 'react'
import useTemplateData from '../hooks/useTemplateData'

const SelectPalette = () => {

    const { templateData, setTemplateData } = useTemplateData()
    const [showColorPicker, setShowColorPicker] = useState<boolean>(false)
    const [currentSelectedColor, setCurrentSelectedColor] = useState<string | null>(null)

    const handleShowColorPicker = (name: string, color: string) => {
        console.log({ name, color })
        setShowColorPicker(!showColorPicker)
        setCurrentSelectedColor(color)
    }

    return (
        <>
            <div>Select Palette</div>
            {templateData.palette.map(item => (
                <div>
                    {item.name} : <div
                        style={{ backgroundColor: item.color, width: '30px', height: '30px' }}
                        onClick={() => handleShowColorPicker(item.name, item.color)}
                    ></div>
                </div>
            ))}
            <div>

            </div>
        </>
    )
}

export default SelectPalette
