import React, { useState } from 'react';

const Card: React.FC = (): JSX.Element => {
    const [selectedId, setSelectedId] = useState<string | null>(null);

    const unSelectAll = () => {
        setSelectedId(null)
    }

    return (
        <div className="">
            <MainStage
                selectedId={selectedId}
                setSelectedId={setSelectedId}
                unSelectAll={unSelectAll}
            />
        </div>
    )
};
export default Card;
