import React, { createContext, useEffect } from 'react'
import { useImmerState } from '@shrugsy/use-immer-state';
import { stageDimensions } from '../utils/defaults';

export const INITIAL_STATE = {
    palette: [
        { name: "primary", color: "#ff0000" },
        { name: "secondary", color: "#00ff00" },
        { name: "tertiary", color: "#0000ff" }
    ],
    variations: [
        {
            name: "untitled",
            face: "",
            // background: {
            //     id: 'shapes_background',
            //     type: "rectangle",
            //     x: 0,
            //     y: 0,
            //     width: stageDimensions.width,
            //     height: stageDimensions.height,
            //     // fill: '#F0F0F0',
            //     fillLinearGradientStartPoint: { x: 0, y: 0 },
            //     fillLinearGradientEndPoint: { x: stageDimensions.width, y: stageDimensions.height },
            //     fillLinearGradientColorStops: [0, '#F0F0F0', 1, '#F0F0F0'],
            //     draggable: false,
            // },
            textBoxes: [],
            images: [],
            svgs: [],
            shapes: [{
                id: 'shapes_background',
                type: "rectangle",
                x: 0,
                y: 0,
                width: stageDimensions.width,
                height: stageDimensions.height,
                // fill: '#F0F0F0',
                fillLinearGradientStartPoint: { x: 0, y: 0 },
                fillLinearGradientEndPoint: { x: stageDimensions.width, y: stageDimensions.height },
                fillLinearGradientColorStops: [0, '#F0F0F0', 1, '#F0F0F0'],
                draggable: false,
            }],
        }
    ],
}

declare const window: any

const localTemplateState = JSON.parse(window.localStorage.getItem("localTemplateState"));

const TemplateContext = createContext(null)
TemplateContext.displayName = 'TemplateContext';

const TemplateProvider = ({ children }) => {

    const [templateData, setTemplateData, { goForward, goBack, stepNum, history }] = useImmerState(localTemplateState || INITIAL_STATE)

    useEffect(() => {
        window.localStorage.setItem("info", JSON.stringify(templateData));
    }, [templateData]);

    return (
        <TemplateContext.Provider
            value={[templateData, setTemplateData, { goForward, goBack, stepNum, history }]}
        >
            {children}
        </TemplateContext.Provider >
    );

}

export { TemplateContext, TemplateProvider }
